import React from "react";
import style from "./style.module.css";
import { useDispatch } from "react-redux";
import { removeFromBasket, updateQuantity } from "../../redux/basketSlice";
import line from "../../images/linebasket.png";
import minus from "../../images/minus.svg";
import plus from "../../images/plus.svg";
import deleteButton from "../../images/delete.svg";

export const BasketItem = ({ product }) => {
  const dispatch = useDispatch();

  const handleRemoveFromBasket = () => {
    dispatch(removeFromBasket(product.id));
  };

  const handleIncrement = () => {
    dispatch(
      updateQuantity({ itemId: product.id, quantity: product.quantity + 1 })
    );
  };

  const handleDecrement = () => {
    if (product.quantity > 1) {
      dispatch(
        updateQuantity({ itemId: product.id, quantity: product.quantity - 1 })
      );
    }
  };

  return (
    <div>
      <img
        className={style.img}
        src={`http://localhost:3333${product.image}`}
        alt={product.title}
      />
      <h3>{product.title}</h3>
      <div>
        <p>{product.price}$</p>
        <p>{product.discont_price}</p>
      </div>
      <div className={style.counter}>
        <button className={style.buttonCounter} onClick={handleDecrement}><img src={minus} alt="-" /></button>
        <p className={style.quantity}> {product.quantity}</p>
        <button className={style.buttonCounter} onClick={handleIncrement}><img src={plus} alt="+" /></button>
      </div>
      <button onClick={handleRemoveFromBasket}><img  src={deleteButton} alt="X"/></button>
      <img src={line} alt="line" className={style.line} />
    </div>
  );
};
