import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateQuantity, removeFromBasket } from "../path-to-your-basketSlice";

const BasketItem = ({ item }) => {
  const dispatch = useDispatch();

  const handleQuantityChange = (newQuantity) => {
    dispatch(updateQuantity({ itemId: item.id, quantity: newQuantity }));
  };

  const handleRemoveFromBasket = () => {
    dispatch(removeFromBasket(item.id));
  };

  return (
    <div>
      <img src={item.image} alt={item.title} />
      <h3>{item.title}</h3>
      <p>Price: {item.price}$</p>
      <p>Quantity: <input type="number" value={item.quantity} onChange={e => handleQuantityChange(e.target.value)} /></p>
      <button onClick={handleRemoveFromBasket}>Remove</button>
    </div>
  );
};

export default BasketItem;
