import { configureStore, combineReducers } from "@reduxjs/toolkit";
import { productsApi } from "./productsApi";
import { setupListeners } from "@reduxjs/toolkit/query";
import basketReducer from "./basketSlice";
import { persistStore } from 'redux-persist'; 
import persistedBasketReducer from './persistConfig';

const rootReducer = combineReducers({
  basket: persistedBasketReducer, // Используйте обернутый редюсер
  [productsApi.reducerPath]: productsApi.reducer,
});

export const store = configureStore({
  reducer: rootReducer, // Передайте корневой редюсер
  middleware: (getDefaultMiddleware) => [
    ...getDefaultMiddleware(),
    productsApi.middleware,
  ],
});

setupListeners(store.dispatch);

export const persistor = persistStore(store); // Создайте persistor для использования в UI
