import React from "react";
import { NavLink } from "react-router-dom";
import style from "./style.module.css";
import { CategoryItem } from "../CategoryItem";
import { useGetAllCategoriesQuery } from "../../store/sliceApi";
import { ErrorPage } from "../../pages/ErrorPage";



export const CatalogHomePage = () => {
  const { data = [], error } = useGetAllCategoriesQuery();
  return (
    <div className={style.container}>
      <div>
        <h2>Catalog</h2>
        <button>
          <NavLink to="/products">All categories</NavLink>
        </button>
      </div>
      {error && <ErrorPage />}
      {
        <ul>
          {data.map(
            (el, ind) => ind < 4 && <CategoryItem key={el.id} {...el} />
          )}
        </ul>
      }
    </div>
  );
};
