import { ProductItem } from "../ProductItem";
import { useGetAllProductsQuery } from "../../redux/productsApi";
import { NavLink } from "react-router-dom";
import style from "./style.module.css";
import { useState } from "react";
//import { Filter } from "../Filter";

export const ProductList = () => {
  const { data = [], error, isLoading } = useGetAllProductsQuery();
  const [showDiscounted, setShowDiscounted] = useState(false);
  const [sortOption, setSortOption] = useState("Default")
  const discountedProducts = data.filter((el) => el.discont_price !== null);
  


  return (
    <div className={style.container}>
      <h2 className={style.h2}>All products</h2>
      <div className={style.container}>
      <div>
        <label className={style.label} htmlFor="Price">
          {" "}
          Price{" "}
        </label>
        <input
          className={style.priceInput}
          inputMode="numeric"
          maxLength="4"
          placeholder="from"
        />
        <input
          className={style.priceInput}
          inputMode="numeric"
          maxLength="4"
          placeholder="to"
        />
      </div>
       <div>
        <label className={style.label}> Discounted items</label>
        <input
          type="checkbox"
          checked={showDiscounted}
          onChange={() => setShowDiscounted(!showDiscounted)}
        />
      </div>
      <div>
        <label className={style.label} htmlFor="sortOption">
          Sorted
        </label>
        <select className={style.select} id="sortOption">
          <option value="Default">Default</option>
          <option value="Ascending">Ascending</option>
          <option value="Descending">Descending</option>
        </select>
      </div>
    </div>
      {isLoading && <p>Loading...</p>}
      {error && <p>Error</p>}
      <ul className={style.ul}>
        {showDiscounted
          ? discountedProducts.map((el) => (
              <NavLink to={`/products/${el.id}`} key={el.id}>
                <ProductItem {...el} />
              </NavLink>
            ))
          : data.map((el) => (
              <NavLink to={`/products/${el.id}`} key={el.id}>
                <ProductItem {...el} />
              </NavLink>
            ))}
      </ul>
    </div>
  );
};
