import React from "react";
import { useParams } from "react-router-dom";
import { useGetProductItemQuery } from "../../redux/productsApi";
import style from "./style.module.css";
import line from "../../images/line.png";


export const SkuPage = () => {
  const { id } = useParams();
  const { data, error, isLoading } = useGetProductItemQuery(id);
  const dates = data && data[0];

  const discount = () => {
    return dates.discont_price && Math.round((dates.price / dates.discont_price - 1) * 100);
  };
  
  return (
    <div>
      {isLoading ? (
        <p>loading...</p>
      ) : error ? (
        <Error/>
      ) : (
        <div>
          <h2 className={style.title}>{dates.title}</h2>
          <div className={style.container}>
            <div>
              <img
                className={style.img}
                src={`http://localhost:3333${dates.image}`}
                alt={dates.title}
              />
            </div>
            <div className={style.productInfo}>
              {dates.discont_price !== null ? (
                <div className={style.priceBox}>
                  <p className={style.bigPrice}>
                    {dates.discont_price}
                    <span className={`${style.span} ${style.isBlack}`}>$</span>
                  </p>
                  <p className={style.smallPrice}>{dates.price}$</p>
                  <p className={style.discount}>-{discount()}
                    <span className={`${style.span} ${style.isPink}`}>%</span>
                  </p>
                </div>
              ) : (
                <div className={style.priceBox}>
                  <p className={style.bigPrice}>
                    {dates.price}
                    <span className={`${style.span} ${style.isBlack}`}>$</span>
                  </p>
                </div>
              )}
              <button className={style.button}>To cart</button>
              <img src={line} alt="line" />
              <div>
                <h3 className={style.h3}>Description</h3>
                <p className={style.description}>{dates.description}</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
