import React from "react";
import style from "./style.module.css";
import { useDispatch } from "react-redux";

export const BasketItem = ({ product }) => {
  const dispatch = useDispatch();

  const handleRemoveFromBasket = () => {
    dispatch(removeFromBasket(product.id)); // Вызываем действие удаления из корзины
  };
  return (
    <div>
      <img className={style.img} src={`http://localhost:3333${product.image}`} alt={product.title} />
      <h3>{product.title}</h3>
      <p>{product.price}$</p>
      <p>{product.discont_price}</p>
      <p>Quantity: {product.quantity}</p>
      <button  onClick={handleRemoveFromBasket} >X</button>
    </div>
  );
};
