import React from "react";
import style from "./style.module.css";
import { NavLink } from "react-router-dom";

export const ProductItem = ({ id, image, title, price, discont_price }) => {
  const discount = () => {
    return discont_price && Math.round((price / discont_price - 1) * 100);
  };

  return (
    <NavLink to={`/products/${id}`}>
    <div className={style.container}>
        <img className={style.img} src={`http://localhost:3333${image}`} alt={title} ><button className={style.button}>Add to cart</button></img>
        
        <div>
          {discont_price !== null ? (
            <div className={style.priceBox}>
              <p className={style.bigPrice}>{discont_price}<span>$</span></p>
              <p className={style.smallPrice}>{price}$</p>
              <p className={style.discount}>-{discount()}%</p>
            </div>
          ) : (
            <h3 className={style.bigPrice}>{price}<span>$</span></h3>
          )}
        </div>
        <h6 className={style.h6}>{title}</h6>
    </div>
    </NavLink>
 
  );
};
