import storage from 'redux-persist/lib/storage'; // Импортируйте тип хранилища

import { persistReducer } from 'redux-persist';
import basketReducer from './basketSlice';

// Конфигурация хранилища
const persistConfig = {
  key: 'root', // Ключ для хранилища
  storage, // Тип хранилища (localStorage, sessionStorage и т.д.)
  // Другие настройки...
};

// Оберните ваш редюсер корзины в persistReducer
const persistedBasketReducer = persistReducer(persistConfig, basketReducer);

export default persistedBasketReducer; // Экспортируйте обернутый редюсер
