import storage from 'redux-persist/lib/storage';
import { persistReducer } from 'redux-persist';
import basketReducer from './basketSlice'; // импортируйте ваш редюсер корзины

const persistConfig = {
  key: 'root', // ключ, по которому будет храниться состояние в localStorage
  storage, // используем localStorage
  whitelist: ['basket'], // список редюсеров, которые вы хотите сохранить (в данном случае "basket")
};

const persistedBasketReducer = persistReducer(persistConfig, basketReducer); // оберните ваш редюсер корзины в persistReducer

export default persistedBasketReducer; // экспортируйте обернутый редюсер
