import React, { useState } from "react";
import style from "./style.module.css";
import gnom from "../../images/gnom.png";

export const GetDiscount = () => {
  const [phoneNumber, setPhoneNumber] = useState("");
  const [isFormSubmitted, setIsFormSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Здесь можете добавить логику для отправки формы и получения ответа.
    // После успешной отправки формы, устанавливаем isFormSubmitted в true
    setIsFormSubmitted(true);
  };

  const handleInput = (e) => {
    const inputPhoneNumber = e.target.value;
    // Используем регулярное выражение для проверки формата номера телефона
    // Допускаются только числа, начинающиеся с "+49" и имеющие длину 13 символов
    const validPhoneNumber = /^(\+49)\d{11}$/.test(inputPhoneNumber);
    if (validPhoneNumber || inputPhoneNumber === "") {
      // Обновляем состояние phoneNumber только в случае, если ввод соответствует условиям
      setPhoneNumber(inputPhoneNumber);
    }
  };

  return (
    <div className={style.getDiscount}>
      <div className={style.getDiscountImage}>
        <img src={gnom} alt="getDiscount" />
      </div>
      <div className={style.getDiscountForm}>
        {isFormSubmitted ? (
          <div>
            <h2 className={style.h2}>Sucsess!</h2>
            <p className={style.p}> Check your SMS</p>
          </div>
        ) : (
          <>
            <h2 className={style.h2}>5% off</h2>
            <p className={style.p}>on the first order</p>
            <form className={style.form} onSubmit={handleSubmit}>
              <input
                className={style.input}
                type="text"
                placeholder="+49"
                pattern="^(\+49)\d{11}$" // Проверка на формат номера телефона
                maxLength="13" 
                value={phoneNumber}
                onInput={handleInput}
              />
              <button className={style.button} type="submit">
                Get a discount
              </button>
            </form>
          </>
        )}
      </div>
    </div>
  );
};
