import React from "react";
import style from "./style.module.css";
import minus from "../../images/minus.svg";
import plus from "../../images/plus.svg";
import line from "../../images/line.png";
import { useDispatch } from "react-redux";
import { removeFromBasket, updateQuantity } from "../../redux/basketSlice";

export const BasketItem = ({ product }) => {
  const dispatch = useDispatch();

  const handleRemoveFromBasket = () => {
    dispatch(removeFromBasket(product.id));
  };

  const handleIncrement = () => {
    dispatch(updateQuantity({ id: product.id, quantity: product.quantity + 1 }));
  };

  const handleDecrement = () => {
    if (product.quantity > 1) {
      dispatch(updateQuantity({ id: product.id, quantity: product.quantity - 1 }));
    }
  };

  return (
    <div className={style.gridContainer}>
      <div className={style.column1}>
        <img className={style.img} src={`http://localhost:3333${product.image}`} alt={product.title} />
      </div>
      <div className={style.column2}>
        <h3>{product.title}</h3>
        <div className={style.counter}>
          <button className={style.buttonCounter} onClick={handleDecrement}>
            <img src={minus} alt="-" />
          </button>
          <p className={style.quantity}> {product.quantity}</p>
          <button className={style.buttonCounter} onClick={handleIncrement}>
            <img src={plus} alt="+" />
          </button>
        </div>
      </div>
      <div className={style.column3}>
        <p className={style.discountPrice}>{product.discont_price}$</p>
      </div>
      <div className={style.column4}>
        <p className={style.regularPrice}>{product.price}$</p>
      </div>
      <div className={style.column5}>
        <button className={style.deleteButton} onClick={handleRemoveFromBasket}>
          X
        </button>
      </div>
      <img src={line} alt="line" className={style.line} />
    </div>
  );
};
