import React, { useState, useEffect } from "react";
import style from "./style.module.css";
import line from "../../images/line.png";
import closeIcon from "../../images/close-icon.svg"; // Путь к изображению крестика

export const ProductInfo = ({ dates, discount }) => {
  const dispatch = useDispatch();
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleAddToCart = () => {
    dispatch(addToBasket(dates));
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  useEffect(() => {
    if (isModalOpen) {
      const timer = setTimeout(() => {
        setIsModalOpen(false);
      }, 4000);

      return () => clearTimeout(timer);
    }
  }, [isModalOpen]);

  return (
    <div>
      {/* Остальное содержание */}
      <button className={style.button} onClick={handleAddToCart}>
        Добавить в корзину
      </button>
      {isModalOpen && (
        <div className={style.modal}>
          <div className={style.modalContent}>
            <button className={style.closeButton} onClick={closeModal}>
              <img src={closeIcon} alt="Close" />
            </button>
            <p>Товар добавлен в корзину!</p>
          </div>
        </div>
      )}
      <img src={line} alt="line" />
      <div>
        {/* Остальное содержание */}
      </div>
    </div>
  );
};
